import clsx from 'clsx';
import { useEffect, useRef, useState } from 'react';
import styles from './App.module.scss';

const snd = new Audio("data:audio/wav;base64,");

// List for https://symphonynovascotia.ca/faqs/symphony-101/how-do-musicians-know-how-fast-to-play-a-piece-and-why-are-the-terms-in-italian/
const getTempo = (beatsPerMinute) => {
  if (beatsPerMinute > 178)  {
    return 'Prestissimo';
  }

  if (beatsPerMinute > 168)  {
    return 'Presto';
  }

  if (beatsPerMinute > 132)  {
    return 'Vivace';
  }

  if (beatsPerMinute > 109)  {
    return 'Allegro';
  }

  if (beatsPerMinute > 98)  {
    return 'Allegretto';
  }

  if (beatsPerMinute > 86)  {
    return 'Moderato';
  }

  if (beatsPerMinute > 73)  {
    return 'Andante';
  }

  if (beatsPerMinute > 65)  {
    return 'Adagietto';
  }

  if (beatsPerMinute > 55)  {
    return 'Adagio';
  }

  if (beatsPerMinute > 45)  {
    return 'Largo';
  }

  if (beatsPerMinute > 40)  {
    return 'Lento';
  }

  return 'Grave';
}

function App() {
  const [numberOfBeats, setNumberOfBeats] = useState(4);
  const [beatsPerMinute, setBeatsPerMinute] = useState(120);
  const [active, setActive] = useState(0);

  const interval = useRef(null);

  useEffect(() => {
    if (interval.current) {
      clearInterval(interval.current);
    }

    interval.current = setInterval(() => {
      setActive(active => (active + 1) % numberOfBeats);
      snd.play();
    }, 60 / beatsPerMinute * 1000);
  }, [numberOfBeats, beatsPerMinute]);

  const handleStopClick = () => {
    if (interval.current) {
      clearInterval(interval.current);
      interval.current = null;
    }
  }

  const handleStartClick = () => {
    if (!interval.current) {
      interval.current = setInterval(() => {
        setActive(active => (active + 1) % numberOfBeats);
        snd.play();
      }, 60 / beatsPerMinute * 1000);
    }
  }

  return (
    <div className={styles.App}>
      Metronome
      <div className={styles.metronome}>

        <div className={styles.bar}>
          {[...Array(numberOfBeats).keys()].map(index => <div className={clsx(styles.item, index === active && styles.active)} key={index}>
            </div>)}
        </div>
      </div>

      
      <div>
        <label htmlFor="numberOfBeats">
          Number of beats
        </label>
        <input id="numberOfBeats" value={numberOfBeats} onChange={(event) => {
          const number = Number(event.target.value)
          
          if (number >= 0) {
            setNumberOfBeats(number);
          }
        }} />
      </div>
      
      <div>
        <label htmlFor="bpm">
          Beats per Minute
        </label>
        <input id="bpm" value={beatsPerMinute} onChange={(event) => {
          const number = Number(event.target.value)
          
          if (number >= 0) {
            setBeatsPerMinute(number);
          }
        }} />
      </div>

      <div className={styles.tempo}>
        {getTempo(beatsPerMinute)}
      </div>

      <button onClick={handleStopClick} className={styles.button}>
        STOP
      </button>

      <button onClick={handleStartClick} className={styles.button}>
        START
      </button>
    </div>
  );
}

export default App;
